<?phpif(!isset($GLOBALS['REQAR'][1])){	header('Location: '._base_url.'/404/');	exit();}unset($parsed_res);$id_order = $GLOBALS['REQAR'][1];$Orders = new Orders();$list = $Orders->GetOrderForPricelist(array("o.id_order"=>$id_order));$tpl->Assign('cat', $list['cats']);$parsed_res = array(	'issuccess'	=> true,	'html'		=> $tpl->Parse($GLOBALS['PATH_tpl'].'cp_pricelist_order.tpl'));if(true == $parsed_res['issuccess']){	$tpl_center .= $parsed_res['html'];}?>